import React from 'react';
// //import '../../App.css';
import './service.css';
import service1 from '../../image/service1.png';
import service2 from '../../image/service2.png';
import service3 from '../../image/service3.png';
import service4 from '../../image/service4.jpeg';
import service5 from '../../image/service5.jpeg';
import service6 from '../../image/service6.png';
export default function Services({onpress}) {
  // const handleClick=()=>{
  // return(
  //   {Card}
  //   );
  // }
  const Card=({data})=>{
    const{id,icon,title,subTitle}=data
    return(
      <div class="card border-success mb-3" style="max-width: 18rem;">
      <div class="card-header bg-transparent border-success">{title}</div>
      <div class="card-body text-success">
        <h5 class="card-title">{icon}</h5>
        <p class="card-text">{subTitle}</p>
      </div>
      <div class="card-footer bg-transparent border-success">
        button
      </div>
    </div>


    );
  }

  const data = [
    {
      id: 1,  
      icon: service1,
      title: "Get Best Prices",
      subTitle:
        "Pay through our application and save thousands and get amazing rewards.",
    },
    {
      id:2,
      icon: service2,
      title: "Covid Safe",
      subTitle:
        "We have all the curated hotels that have all the precaution for a covid safe environment.",
    },
    { 
      id:3,
      icon: service3,
      title: "Flexible Payment",
      subTitle:
        " Enjoy the flexible payment through our app and get rewards on every payment.",
    },
    { 
      id:4,
      icon: service4,
      title: "Find The Best Near You",
      subTitle:
        "Find the best hotels and places to visit near you in a single click.",
    },
    { id:5,
      icon: service5,
      title: "Hotel Booking",
      subTitle:
        "Find the best hotels and places to visit near you in a single click.",
    },
    { id:6,
      icon: service6,
      title: "Airline Booking",
      subTitle:
        "Find the best hotels and places to visit near you in a single click.",
    }
  ];
   return (

      <>
      <div>
        <h1 className='headclass'>

          OUR TRAVEL SERVICES
        </h1>


      </div>







    <section id="services">
      {data.map((service, index) => {

        return (
          <>
          <div className="service"  onClick={()=>Card}>
            <ul className="icon"  >

            <img src={service.icon} alt=""  />


            </ul>


            <h3>{service.title}</h3>
            {/* <p>{service.subTitle}</p> */}

          </div>




          </>
        );
      })}
    </section>
    </>

  )
}
